    <div>
      <div>{count}</div>
      <button
        onClick={() => {
          setCount(count + 1);
        }}
      >
        +
      </button>
    </div>
  );
}

function useUpdate() {
  const ref = useRef(false);
  useEffect(() => {
    ref.current = true;
  });
  return ref.current;
}
```

